INSERT INTO produit VALUES(1, "Boule_de_cristal", 99.99, 1, 10, "orange", "boule-de-cristal.png", "boule-de-cristal.png", "Boule de cristal", 1, "Les Dragon Balls sont de petites boules qui peuvent tenir dans une main, de couleur orange et qui possèdent chacune un nombre d'étoiles, allant de un à sept. ");
INSERT INTO produit VALUES(2, "Bandeau_konoha", 5, 1, 10, "noir", "bandeau-konoha.png", "bandeau-konoha.png", "Bandeau frontal de Konoha", 2, "Un bandeau frontal (額当て, Hitai-ate) est un accessoire en tissu sur lequel est fixée une plaque de métal où est gravé le symbole d'un des villages ninjas. Ils servent donc à indiquer l'appartenance militaire de leurs porteurs.");
INSERT INTO produit VALUES(3, "Carte_hxh", 7.99, 1, 10, "bleu", "carte-hxh.jpg", "carte-hxh.jpg", "Licence de Hunter", 2, "Une licence de Hunter permet à son utilisateur de prouver qu'il est Hunter. Une licence vaut environ 100 millions de Jenis et ne peut être falsifiée. Elle permet d’accéder à 95% des organismes publics gratuitement et permet également d'entrer dans 90% des pays + 75% des régions interdites. La licence permet également d'accéder au site des hunters, rempli d'informations confidentielles.");
INSERT INTO produit VALUES(4, "Costume_opm", 15, 1, 10, "jaune", "costume-opm.png", "costume-opm.png", "Costume de Saitama", 3, "Le costume de Saitama (サイタマ, Saitama) est le protagoniste principal de One Punch-Man et le personnage le plus puissant de l'histoire.");
INSERT INTO produit VALUES(5, "Couteau_ac", 40, 1, 10, "vert", "couteau-ac.jpg", "couteau-ac.jpg", "Couteau spécial", 1, "L'unique couteau permettant de donner un coup fatal à Kuro-Sensei");
INSERT INTO produit VALUES(6, "Death_Note", 50.99, 1, 10, "noir", "death-note.jpg", "death-note.jpg", "Death Note", 2, "Son mode d'emploi indique que « la personne dont le nom est écrit dans ce cahier meurt ».");
INSERT INTO produit VALUES(7, "Dispelda", 5, 1, 10, "gris", "dispelda.jpg", "dispelda.jpg", "Dispelda", 2, "Dispelda (断魔の剣, Danma no Tsurugi) est une ancienne Arme d'Anti-Magie. L'épée est invoquée d'un grimoire ouvert, dans lequel elle est stockée sous forme d'écritures. L'utilisateur peut aisément les invoquer sans consommer de pouvoir magique.");
INSERT INTO produit VALUES(8, "Epee_Meliodas", 32.5, 1, 10, "gris", "epee-meliodas.png", "epee-meliodas.png", "Trancheuse de Démon Lostvayne", 2, "La Trancheuse de Démon Lostvayne「魔剣 ロストヴェイン Maken Rosutovein」 est le Trésor Sacré de Meliodas.");
INSERT INTO produit VALUES(9, "Epee_tridimensionelle", 10, 1, 10, "noir", "epee-tridimensionnelle.png", "epee-tridimensionnelle.png", "Épée tridimensionnelle", 1, "Le sabre coupe-nuque est l'arme la plus efficace pour tuer un Titan. La lame ne peut être utilisée qu'une seule fois. Elle mesure 80 cm. Les munitions (les autres lames) sont situées dans des fourreaux accrochés aux cuisses de l'utilisateur.");
INSERT INTO produit VALUES(10, "Gant_dxd", 10, 1, 10, "rouge", "gant-hdxd.png", "gant-hdxd.png", "Gant de l'Empereur Dragon rouge", 2, "Relique sacrée appartenant à Ddraig.");
INSERT INTO produit VALUES(11, "Gants_Izuku", 10, 1, 10, "vert", "gant-izuku.jpg", "gant-izuku.jpg", "Gants Air Force", 3, "Les Gants Air Force est un équipement de soutien créé par Mei Hatsume pour Izuku Midoriya.");
INSERT INTO produit VALUES(12, "Katana_rengoku", 10, 1, 10, "black", "kanata-rengoku.jpg", "kanata-rengoku.jpg", "Katana de Rengoku", 2, "Le katana de Kyojuro Rengoku (煉れん獄ごく 杏きょう寿じゅ郎ろう Rengoku Kyôjurô), pourfendeur de démons et Pilier de la Flamme");
INSERT INTO produit VALUES(13, "Maillot_bluelock", 10, 1, 10, "bleu", "maillot-bluelock.jpeg", "maillot-bluelock.jpeg", "Maillot du Japon (Blue Lock)", 3, "Maillot du Japon lors de la coupe du monde de 2023. Il a été inspiré par le maillot des footballeurs du manga Blue Lock");
INSERT INTO produit VALUES(14, "Masque_kaneki", 10, 1, 10, "noir", "masque-kaneki.jpg", "masque-kaneki.jpg", "Masque de Ken Kaneki", 2, "Confectionné par Uta, ce masque iconique appartient à la goule Cache-Oeil : Ken Kaneki.");
